    }

    /**
     * @return {!Array<!GridColumnElement>}
     * @protected
     */
    _getColumnsInOrder() {
      return this._columnTree
        .slice(0)
        .pop()
        .filter((c) => !c.hidden)
        .sort((b, a) => b._order - a._order);
    }

    /**
     * @param {number} x
     * @param {number} y
     * @return {HTMLElement | undefined}
