/**
 * @param {!HTMLElement} element
 */
function removeBuildComment(element) {
  const buildComment =
    element.localName === 'template'
      ? /** @type {!HTMLTemplateElement} */ (element).content.firstChild
      : element.firstChild;
  buildComment.parentNode.removeChild(buildComment);
}

