class MonthCalendarElement extends ThemableMixin(GestureEventListeners(PolymerElement)) {
  static get template() {
    return html`
      <style>
        :host {
          display: block;
        }

        [part='weekdays'],
        #days {
          display: flex;
          flex-wrap: wrap;
          flex-grow: 1;
        }

        #days-container,
        #weekdays-container {
          display: flex;
        }

        [part='week-numbers'] {
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          flex-shrink: 0;
        }

        [part='week-numbers'][hidden],
        [part='weekday'][hidden] {
          display: none;
        }

        [part='weekday'],
        [part='date'] {
          /* Would use calc(100% / 7) but it doesn't work nice on IE */
          width: 14.285714286%;
        }

        [part='weekday']:empty,
        [part='week-numbers'] {
          width: 12.5%;
          flex-shrink: 0;
        }
      </style>

      <div part="month-header" role="heading">[[_getTitle(month, i18n.monthNames)]]</div>
      <div id="monthGrid" on-tap="_handleTap" on-touchend="_preventDefault" on-touchstart="_onMonthGridTouchStart">
        <div id="weekdays-container">
          <div hidden$="[[!_showWeekSeparator(showWeekNumbers, i18n.firstDayOfWeek)]]" part="weekday"></div>
          <div part="weekdays">
            <template
              is="dom-repeat"
              items="[[_getWeekDayNames(i18n.weekdays, i18n.weekdaysShort, showWeekNumbers, i18n.firstDayOfWeek)]]"
            >
              <div part="weekday" role="heading" aria-label$="[[item.weekDay]]">[[item.weekDayShort]]</div>
            </template>
          </div>
        </div>
        <div id="days-container">
          <div part="week-numbers" hidden$="[[!_showWeekSeparator(showWeekNumbers, i18n.firstDayOfWeek)]]">
            <template is="dom-repeat" items="[[_getWeekNumbers(_days)]]">
              <div part="week-number" role="heading" aria-label$="[[i18n.week]] [[item]]">[[item]]</div>
            </template>
          </div>
          <div id="days">
            <template is="dom-repeat" items="[[_days]]">
              <!-- prettier-ignore -->
              <div
                part="date"
                today$="[[_isToday(item)]]"
                selected$="[[_dateEquals(item, selectedDate)]]"
                focused$="[[_dateEquals(item, focusedDate)]]"
                date="[[item]]"
                disabled$="[[!_dateAllowed(item, minDate, maxDate)]]"
                role$="[[_getRole(item)]]"
                aria-label$="[[_getAriaLabel(item)]]"
                aria-disabled$="[[_getAriaDisabled(item, minDate, maxDate)]]">[[_getDate(item)]]</div>
            </template>
          </div>
        </div>
      </div>
    `;
  }

  static get is() {
    return 'vaadin-month-calendar';
  }

  static get properties() {
    return {
      /**
       * A `Date` object defining the month to be displayed. Only year and
       * month properties are actually used.
       */
      month: {
        type: Date,
        value: new Date()
      },

      /**
       * A `Date` object for the currently selected date.
       */
      selectedDate: {
        type: Date,
        notify: true
      },

      /**
       * A `Date` object for the currently focused date.
       */
      focusedDate: Date,

      showWeekNumbers: {
        type: Boolean,
        value: false
      },

      i18n: {
        type: Object
      },

      /**
       * Flag stating whether taps on the component should be ignored.
       */
      ignoreTaps: Boolean,

      _notTapping: Boolean,

      /**
       * The earliest date that can be selected. All earlier dates will be disabled.
       */
      minDate: {
        type: Date,
        value: null
      },

      /**
       * The latest date that can be selected. All later dates will be disabled.
       */
      maxDate: {
        type: Date,
        value: null
      },

      _days: {
        type: Array,
        computed: '_getDays(month, i18n.firstDayOfWeek, minDate, maxDate)'
