    this.__setOrToggleAttribute('aria-labelledby', label ? labelId : undefined, this);
  }

  /** @private */
  __getErrorMessageAriaHidden(invalid, errorMessage, errorId) {
    return (!(errorMessage && invalid ? errorId : undefined)).toString();
