        value: () => [],
        notify: true
      },

      /**
       * Error to show when the input value is invalid.
       * @attr {string} error-message
       */
      errorMessage: {
        type: String,
        value: '',
        observer: '_errorMessageChanged'
      },

      /**
       * String used for the helper text.
       * @attr {string} helper-text
       * @type {string | null}
