      helperText: {
        type: String,
        value: '',
        observer: '_helperTextChanged'
      },

      /**
       * Specifies that the user must fill in a value.
       */
      required: {
        type: Boolean,
        reflectToAttribute: true
      },

      /**
       * This property is set to true when the control value is invalid.
       * @type {boolean}
