  static get observers() {
    return ['_updateOverflow(items.*)'];
  }

  ready() {
    super.ready();
    this.addEventListener('iron-resize', () => this._updateOverflow());
    this._scrollerElement.addEventListener('scroll', () => this._updateOverflow());
