  static get is() {
    return 'vaadin-integer-field';
  }

  static get version() {
    return '3.0.2';
  }

  static get properties() {
    // Hide inherited props that don't work with <input type="number"> from JSDoc.
    return {
      /** @private */
      pattern: String,

      /** @private */
      preventInvalidInput: Boolean,

      /** @private */
      minlength: Number,

      /** @private */
      maxlength: Number
    };
  }

  /** @protected */
